section "game/modules/tome/data/general/objects/knifes.lua"
t("Sharp, short and deadly.", "날카롭고, 짧지만, 치명적입니다.")
t("iron dagger", "무쇠 단검")
t("steel dagger", "강철 단검")
t("dwarven-steel dagger", "드워프강철 단검")
