section "game/modules/tome/data/zones/arena/zone.lua"
t("The Arena", "투기장")
t("#GOLD#Miniboss round starts!!", "#GOLD#중간보스 시합 시작!!")
t("#VIOLET#Boss round starts!!!", "#VIOLET#보스 시합 시작!!!")
t("#LIGHT_RED#Final round starts!!!!", "#LIGHT_RED#최종 시합 시작!!!!")
t("Master of Arena", "투기장의 지배자")
t("nobody", "아무 것도 아님")
t(" the ", " - ")
t("nobody", "무명")
t("rat stomper", "생쥐 학살자")
t("bear beater", "곰 몰이꾼")
t("aspirant", "전사 지망생")
t("fighter", "전사")
t("bold", "용감한 전사")
t("gladiator", "검투사")
t("brave", "용맹한 검투사")
t("dangerous", "위험한 자")
t("promise", "장래가 촉망되는 자")
